import { MDBDataTable } from "mdbreact";
import { React, useState, useEffect } from 'react';

const DT = (props) => {  
    const [RenderTable, setRender] = useState([]);

    let deletePost = (postId) => {
        alert('masuk')
        /* fetch(`https://jsonplaceholder.typicode.com/posts/${postId}`, {
          method: "DELETE",
        })
          .then((res) => res.json())
          .then((res) => {
            var postIndex = posts.findIndex(function (o) {
              return o.id === postId;
            });
            if (postIndex !== -1) {
              setPosts(posts.filter((item) => item.id != postId));
            }
          }); */
    };

    useEffect(() => {
        let postsArray = JSON.parse(JSON.stringify(props.posts));
        let userData = [];
        postsArray.map((item, index) => {
            item.id = (
            <div style={{ fontWeight: "bold", fontSize: "1.2em" }}>{item.id}</div>
            );
            item.action = (
            <div style={{ display: "flex", justifyContent: "space-between" }}>
                <div
                className="uil-trash-alt"
                style={{
                    cursor: "pointer",
                    color: "black",
                    fontSize: ".7em",
                    padding: ".5rem",
                    borderRadius: ".3rem",
                    background: "#fb6262",
                }}
                onClick={() => deletePost(props.posts[index].id)}
                >
                Delete
                </div>
            </div>
            );
            userData.push(item);
        });
        setRender(userData);
        }, [props.posts]);

    return (
        <div>
            <MDBDataTable
            responsive
            bordered
            data={props.data}
            searching={true}
            paging={true}
            info={false}
            />
        </div>
    )
}

export default DT;
