import { useNavigate, useParams } from 'react-router-dom';
import { React, useState, useEffect } from 'react';
import axios from 'axios';
import DT from '../table';
import { MDBDataTable } from "mdbreact";

const Index = (props) => {
    const navigate = useNavigate();
    const [loadData, setData] = useState({ data:[] });
	const [LoadDetail, setDetail] = useState({ data:[] });
	const [result, setresult] = useState({ success:false });
	const [showmessage, setmessage] = useState({ message:"" });

    const [posts, setPosts] = useState([]);
    const [usersForRender, setUsersForRender] = useState([]);

    useEffect(() => {
        fetch("http://127.0.0.1:4000/GET")
        .then((res) => res.json())
        .then((res) => {
            setPosts(res.data);
        });
    }, []);

    let deletePost = (postId) => {
        alert('masuk')
        /* fetch(`https://jsonplaceholder.typicode.com/posts/${postId}`, {
          method: "DELETE",
        })
          .then((res) => res.json())
          .then((res) => {
            var postIndex = posts.findIndex(function (o) {
              return o.id === postId;
            });
            if (postIndex !== -1) {
              setPosts(posts.filter((item) => item.id != postId));
            }
          }); */
      };

    const data = {
        columns: [
          {
            label: "#",
            field: "id",
            sort: "asc",
            width: 150,
          },
          {
            label: "Title",
            field: "title",
            sort: "asc",
            width: 270,
          },
    
          {
            label: "Body",
            field: "body",
            sort: "asc",
            width: 200,
          },
          {
            label: "Action",
            field: "action",
            width: 100,
          },
        ],
    
        rows: usersForRender,
    };

    useEffect(() => {
        let postsArray = JSON.parse(JSON.stringify(posts));
        let userData = [];
        postsArray.map((item, index) => {
            item.id = (
            <div style={{ fontWeight: "bold", fontSize: "1.2em" }}>{item.id}</div>
            );
            item.action = (
            <div style={{ display: "flex", justifyContent: "space-between" }}>
                <div
                className="uil-trash-alt"
                style={{
                    cursor: "pointer",
                    color: "black",
                    fontSize: ".7em",
                    padding: ".5rem",
                    borderRadius: ".3rem",
                    background: "#fb6262",
                }}
                onClick={() => deletePost(posts[index].id)}
                >
                Delete
                </div>
            </div>
            );
            userData.push(item);
        });
        setUsersForRender(userData);
    }, [posts]);

    return (
        <div class="container">
            <MDBDataTable
                responsive
                bordered
                data={data}
                searching={true}
                paging={true}
                info={false}
            />
        </div>
    )
}

export default Index;
