import { useNavigate, useParams, Link } from 'react-router-dom';
import { React, useState, useEffect } from 'react';
import axios from 'axios';

import { Navigate } from "react-router-dom";

const Form = (props) => {
    const navigate = useNavigate();
    const { id } = useParams();
    const [command, setCommand] = useState('');
    const [result, setresult] = useState({ success:false });
	const [title, setTitle] = useState('');
	const [qty, setQty] = useState('');

    const ChangeRouteChild = (event) => {
		props.ChangeRoute(event.target.value);
	}

    useEffect(() => {
        if(props.cUrl != '/tambah'){
            var url = 'http://127.0.0.1:4000/GET/' + id;
            fetch(url, {method: "GET",})
            .then((res) => res.json())
            .then((res) => {
                alert('masuk')
                setresult(res.data.success);
                setTitle(res.data.data[0].name);
                setQty(res.data.data[0].qty);
                setCommand('edit');
            })
            .catch(() => {
                alert('error while load data')
            });
        }
        else{
            setCommand('tambah');
        }
	}, [props.cUrl]);

    return (
        <div className='container'>
            <div className="text-center">
                Form {title}
            </div>
            <div className="d-flex justify-content-between">
                <div>
                    <Link to="/" className="btn btn-danger" value="/" onClick={ ChangeRouteChild }>Back</Link>
                </div>
                <div>
                    <button className="btn btn-primary" value="/update">Update</button>
                </div>
            </div>
        </div>
    )
}

export default Form;
